import React, {useState, useContext} from "react";
import AuthContext from "../../api/helper/AuthProvider";
import {doLogin} from "../../api/AuthApi";


const Login = () => {
    const {setAuth} = useContext(AuthContext);
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    // const [jwt, setJwt] = useLocalState("", "jwt");

    function sendLoginRequest () {
        try {
            doLogin(username, password).then((response => {
                const accessToken = response.accessToken;
                window.location.href = '/'
            }));
        } catch (err) {

        }


    }


    return (
        <>
            <div>
                <label htmlFor="username">Username</label>
                <input type="email" id="username" value={username} onChange={(event) => setUsername(event.target.value)}/>
            </div>
            <div>
                <label htmlFor="password">Password</label>
                <input type="password" id="password" value={password} onChange={(event) => setPassword(event.target.value)}/>
            </div>
            <div>
                <button id="submit" type="button" onClick={() => sendLoginRequest()}>
                    Login
                </button>

            </div>

        </>

    )
}

export default Login;
